struct PRESTypePluginPool {

    int encapsulationCount;
    RTIEncapsulationId *encapsulations; /* array */

    struct REDAFastBufferPool *samplePool;

    /* Samples from writerLoanedSamplePool are managed via the two lists
     * inUseWriterLoanedSamples and freeWriterLoanedSamples.
     *
     * freeWriterLoanedSamples contains samples which are discarded(discardLoan)
     *     or removed via on_sample_removed callback.
     * inUseWriterLoanedSamples contains samples which are are returned by
     *     getLoan operation
     *
     * During TypePlugin pool deletion, samples from both these lists are
     * returned back to the pool before deleting the lists and the pool
     * */
    struct REDAFastBufferPool *writerLoanedSamplePool;
    struct REDAInlineList *inUseWriterLoanedSamples;
    struct REDAInlineList *freeWriterLoanedSamples;
    unsigned int _writerLoanedSampleMaxSize;

    struct REDAFastBufferPool *keyPool;
    struct PRESTypePluginEndpointInfo endpointInfo;

    /* arrays of size encapsulationCount */
    struct REDAFastBufferPool **bufferPool;
    int *bufferPoolThreshold;
    int *writerBufferDynamicMaxSize; /* -2=unset, -1=unlimited, >0 */
    int *writerBufferDynamicCurrSize;/* Current size */
    unsigned int *writerBufferBatchSize;      /* 0=batch. disabled, >0=batch. enabled*/
    
    PRESTypePluginDefaultEndpointDataCreateSampleFunction 
        createSampleFnc;
    PRESTypePluginDefaultEndpointDataDestroySampleFunction 
        deleteSampleFnc;

    PRESTypePluginDefaultEndpointDataCreateKeyFunction 
        createKeyFnc;
    PRESTypePluginDefaultEndpointDataDestroyKeyFunction 
        deleteKeyFnc;
    PRESTypePluginGetSerializedSampleSizeFunction
        getSerializedSampleSizeFnc;
    /* Argument to pass to getSerializedSampleSizeFnc as endpointData */
    PRESTypePluginParticipantData getSerializedSampleSizeFncArg;

    /* initialize and destroy sample with parameter */
    REDAFastBufferPoolBufferInitializeFunction createSampleWithParamFnc;
    void* createSampleParam;
    REDAFastBufferPoolBufferFinalizeFunction deleteSampleWithParamFnc;
    void* deleteSampleParam;

    REDAFastBufferPoolBufferInitializeFunction createKeyWithParamFnc;
    void* createKeyParam;
    REDAFastBufferPoolBufferFinalizeFunction deleteKeyWithParamFnc;
    void* deleteKeyParam;
};


struct PRESTypePluginVersion {
    char majorRev;
    char minorRev;
    char majorOptions;
    char minorOptions;
};

/* plugin version copied from nddsgen module version */

/* CURRENT VERSION: 2.0 */
#define PRES_TYPE_PLUGIN_VERSION_1_0  {1, 0, 0, 0}
#define PRES_TYPE_PLUGIN_VERSION_1_1  {1, 1, 0, 0}
#define PRES_TYPE_PLUGIN_VERSION_2_0  {2, 0, 0, 0}
#define PRES_TYPE_PLUGIN_VERSION_CURRENT  PRES_TYPE_PLUGIN_VERSION_2_0


struct PRESTypePlugin {
    /* Callbacks */
    PRESTypePluginOnParticipantAttachedCallback
        onParticipantAttached;
    PRESTypePluginOnParticipantDetachedCallback
        onParticipantDetached;
    PRESTypePluginOnEndpointAttachedCallback
        onEndpointAttached;
    PRESTypePluginOnEndpointDetachedCallback
        onEndpointDetached;

    /* Sample manipulation */
    PRESTypePluginCopySampleFunction
        copySampleFnc;
    PRESTypePluginCreateSampleFunction
        createSampleFnc;
    PRESTypePluginDestroySampleFunction
        destroySampleFnc;
    PRESTypePluginFinalizeOptionalMembersFunction
        finalizeOptionalMembersFnc;

    /* Compute key hash */
    PRESTypePluginInstanceToKeyHashFunction
        instanceToKeyHashFnc;
    PRESTypePluginSerializedSampleToKeyHashFunction
        serializedSampleToKeyHashFnc;

    /* Serialize */
    PRESTypePluginSerializeFunction
        serializeFnc;
    
    /* Deserialize */
    PRESTypePluginDeserializeFunction
        deserializeFnc;

    /* Sample */
    PRESTypePluginGetSampleFunction
        getSampleFnc;
    PRESTypePluginReturnSampleFunction
        returnSampleFnc;

    /* Sizing */
    PRESTypePluginGetSerializedSampleMaxSizeFunction
        getSerializedSampleMaxSizeFnc;
    PRESTypePluginGetSerializedSampleMinSizeFunction
        getSerializedSampleMinSizeFnc;

    PRESTypePluginGetSerializedSampleSizeFunction
        getSerializedSampleSizeFnc;

    PRESTypePluginGetDeserializedSampleMaxSizeFunction
        getDeserializedSampleMaxSizeFnc;

    /* Key kind */
    PRESTypePluginGetKeyKindFunction
        getKeyKindFnc;

    /**********************************************
    **** Functions only needed for keyed types ****
    **********************************************/

    /* Keys */
    PRESTypePluginGetKeyFunction
        getKeyFnc;
    PRESTypePluginReturnKeyFunction
        returnKeyFnc;

    /* Sizing */
    PRESTypePluginGetSerializedKeyMaxSizeFunction
        getSerializedKeyMaxSizeFnc;

    /* Instance <=> Key */
    PRESTypePluginInstanceToKeyFunction
        instanceToKeyFnc;
    PRESTypePluginKeyToInstanceFunction
        keyToInstanceFnc;

    /* Key (de)serialize */
    PRESTypePluginSerializeKeyFunction
        serializeKeyFnc;
    PRESTypePluginDeserializeKeyFunction
        deserializeKeyFnc;
    PRESTypePluginDeserializeKeySampleFunction
        deserializeKeySampleFnc;

    PRESTypePluginSerializedKeyToKeyHashFunction
        serializedKeyToKeyHashFnc;
    
    /*e Type Code associated to the type*/
    struct RTICdrTypeCode *typeCode;

    /*e 
     * The type Code associated to the type that should be used for filtering. 
     * This field is currently only used by the DynamicData type plugin
     * because there are potentially 2 versions of the type: the original and
     * one with alias and pointer information removed. The original type should
     * be used for filtering purposes and the minimal tc will be pointed at
     * by the plugin's typeCode field. 
     */
    struct RTICdrTypeCode *cftTypeCode;
    /*i Opaque buffer for storing user data */
    PRESWord* _userBuffer;
    /*e Plugin kind */
    PRESTypePluginLanguageKind languageKind;
    /*i Version */
    struct PRESTypePluginVersion version;
    
    /*e Buffer allocator for serialization */
    
    /*e Allocator for the serialized buffer */
    PRESTypePluginGetBufferFunction getBuffer;
    NDDS_WriterHistory_Allocator_getBufferWithParams_fn getBufferWithParams;
    
    /*e Return buffer allocated by getBuffer */
    PRESTypePluginReturnBufferFunction returnBuffer;
    NDDS_WriterHistory_Allocator_returnBufferWithParams_fn returnBufferWithParams;

    /* Functions needed to support writer loaned samples */
    PRESTypePluginGetWriterLoanedSampleFunction getWriterLoanedSampleFnc;
    PRESTypePluginReturnWriterLoanedSampleFunction returnWriterLoanedSampleFnc;
    PRESTypePluginReturnWriterLoanedSampleFromCookieFunction
        returnWriterLoanedSampleFromCookieFnc;
    PRESTypePluginValidateWriterLoanedSampleFunction
        validateWriterLoanedSampleFnc;
    PRESTypePluginSetWriterLoanedSampleSerializedStateFunction
        setWriterLoanedSampleSerializedStateFnc;

    /*i Unique type name */
    const char *endpointTypeName;

    /*i type code name */
    const char *typeCodeName;
    
    /* This pointer is reserved for the middleware */
    void * reserved;

    /* Set to true in generated code if metp code is generated for
     * this type */
    RTIBool isMetpType;
};


